import React, { useState, useEffect } from 'react';
import { getFirestore, collection, onSnapshot } from 'firebase/firestore';
import app from '../firebaseConfig';
import './Navbar.css';

const db = getFirestore(app);

const Navbar = () => {
    const [waitlistCount, setWaitlistCount] = useState(0);

    useEffect(() => {
        const unsubscribe = onSnapshot(collection(db, 'waitlist'), (snapshot) => {
            setWaitlistCount(snapshot.size);
        });

        // Cleanup subscription on unmount
        return () => unsubscribe();
    }, []);

    return (
        <nav className="navbar">
            <div className="navbar-logo">
                Clufree <span className="beta-tag">Beta</span>
            </div>
            <div className="navbar-links">
                <div className="waitlist-count">{waitlistCount}</div>
                <button className="join-waitlist-btn">Join waitlist</button>
            </div>
        </nav>
    );
};

export default Navbar;
